"use strict";

function initializeBasicClipboard() {
  new ClipboardJS("#clipboardBasicTrigger", {
    text: function text() {
      return document.getElementById("clipboardBasic").value;
    }
  }).on("success", function (e) {
    e.clearSelection();
    document.getElementById("clipboardBasic").select();
    var t = document.getElementById("clipboardBasicTrigger");
    t.textContent = "Copied!", setTimeout(function () {
      t.textContent = "Copy";
    }, 3e3);
  });
}
function initializeCutClipboard() {
  var e = document.getElementById("clipboardCut"),
    t = document.getElementById("clipboardCutTrigger");
  t.addEventListener("click", function () {
    e.select(), document.execCommand("cut"), t.textContent = "Copied!", setTimeout(function () {
      t.textContent = "Cut to clipboard";
    }, 3e3);
  });
}
function initializePredefinedClipboard() {
  var t = document.getElementById("clipboardPredefinedTrigger"),
    e = new ClipboardJS(t);
  e.on("success", function (e) {
    t.textContent = "Copied!", setTimeout(function () {
      t.textContent = "Cut to clipboard";
    }, 3e3);
  }), e.on("error", function (e) {
    console.error("Action:", e.action), console.error("Trigger:", e.trigger);
  });
}
function initializeCustomClipboard() {
  new ClipboardJS("#clipboardCustomTrigger", {
    text: function text() {
      return document.getElementById("clipboardCustom").value;
    }
  }).on("success", function (e) {
    e.clearSelection();
    var t = document.getElementById("clipboardCustom"),
      i = document.getElementById("clipboardCustomTrigger"),
      o = document.getElementById("copyIcon");
    o.setAttribute("data-feather", "check"), i.classList.remove("btn-soft-primary"), i.classList.add("btn-success"), t.style.borderColor = "#28a745", t.style.color = "#28a745", setTimeout(function () {
      o.setAttribute("data-feather", "copy"), i.classList.remove("btn-success"), i.classList.add("btn-soft-primary"), t.style.borderColor = "", t.style.color = "";
    }, 3e3);
  });
}
document.addEventListener("DOMContentLoaded", function () {
  initializeBasicClipboard(), initializeCutClipboard(), initializePredefinedClipboard(), initializeCustomClipboard();
});