"use strict";

function initSimpleDatePicker() {
  $(function () {
    $('input[name="simpleDatePicker"]').daterangepicker({
      opens: "right",
      cancelClass: "btn-light"
    }, function (t, e, n) {
      console.log("A new date selection was made: " + t.format("YYYY-MM-DD") + " to " + e.format("YYYY-MM-DD"));
    });
  });
}
function initSingleDatePicker() {
  $('input[name="singleDatePicker"]').daterangepicker({
    opens: "right",
    cancelClass: "btn-light",
    singleDatePicker: !0,
    showDropdowns: !0,
    minYear: 1901,
    maxYear: parseInt(moment().format("YYYY"), 10)
  }, function (t, e, n) {
    t = moment().diff(t, "years");
    alert("You are " + t + " years old!");
  });
}
function initPredefinedDateRanges() {
  var t = moment().subtract(29, "days"),
    e = moment();
  function n(t, e) {
    $("#predefinedDateRanges input.input-filed").html(t.format("MMMM D, YYYY") + " - " + e.format("MMMM D, YYYY"));
  }
  $("#predefinedDateRanges").daterangepicker({
    opens: "right",
    cancelClass: "btn-light",
    startDate: t,
    endDate: e,
    ranges: {
      Today: [moment(), moment()],
      Yesterday: [moment().subtract(1, "days"), moment().subtract(1, "days")],
      "Last 7 Days": [moment().subtract(6, "days"), moment()],
      "Last 30 Days": [moment().subtract(29, "days"), moment()],
      "This Month": [moment().startOf("month"), moment().endOf("month")],
      "Last Month": [moment().subtract(1, "month").startOf("month"), moment().subtract(1, "month").endOf("month")]
    }
  }, n), n(t, e);
}
initSimpleDatePicker(), initSingleDatePicker(), initPredefinedDateRanges();