"use strict";

function initSingleSelect() {
  $('[data-select="single"]').select2({
    placeholder: "--select--"
  });
}
function initMultipleSelect() {
  $('[data-select="multiple"]').select2({
    tags: !0,
    closeOnSelect: !1,
    placeholder: "--select--"
  });
}
